<template>
  <div class="new-comment">

    <form 
          method="post"
          v-on:submit.prevent="postComment"
          action="/.netlify/functions/comments"
          data-netlify="true"
          data-netlify-honeypot="bot-field"
          ref="commentform"
        >
      <p hidden>
        <label>
          Donâ€™t fill this out: <input name="bot-field" />
        </label>
      </p>
      <input type="hidden" name="redirect" id="redirect" value="https://baldbeardedbuilder.com/thanks/"/>
      <input type="hidden" name="avatar" id="avatar" ref="avatar" />
      <input type="hidden" name="postpath" id="postpath" :value="path"/>

      <div class="avatar">
          <img
            src="data:image/gif;base64,"
            data-fallbacksrc="/images/comments/unknown-avatar.png"
            data-role="user-avatar"
            alt="avatar"
            id="avatarPreview"
            ref="avatarPreview"
          />
      </div>
         
      <div id="commentstatus" class="status" ref="commentstatus"></div>
  
      <ul class="flex-outer">
        <li>
          <label for="message">Comment<br/><span class="required">* required</span></label>
          <textarea rows="6" 
                id="message" 
                name="message"
                required
                v-model="formData.message" 
                placeholder="Your message"></textarea>
        </li>
        <li>
          <label for="name">Your Name<br/><span class="required">* required</span></label>
          <input type="text" 
                id="name" 
                name="name"
                required
                placeholder="Enter your name here"
                v-model="formData.name">
        </li> 
        <li>
          <label for="identity">Email/GitHub<br/><span class="required">* required</span></label>
          <input type="text" 
                id="identity" 
                name="identity"
                v-on:change="checkAvatar"
                required
                placeholder="Your email address or GitHub username"
                v-model="formData.identity">
        </li>
        <li>
          <button type="submit" 
            id="comment"
            ref="commentbutton">Leave Comment</button>
        </li>
      </ul>
    </form>
  </div>
</template>
<script>
const md5 = require('md5');

export default {
name: "CommentNew",
props: ["path"],
data() {
  return {
    formData: {},
  }
},
methods: {
  checkAvatar() {
    const emailRegex = /[^@\s]+@[^@\s]+\.[^@\s]+$/;
    let possibleAvatars = [];

    if (this.formData.identity.match(emailRegex)) {
      possibleAvatars.push(
        `https://secure.gravatar.com/avatar/${md5(
          this.formData.identity
        )}?s=80&d=identicon&r=pg`
      );
    } else {
      const cleanIdentity = this.formData.identity.replace('@','');
      possibleAvatars.push(`https://github.com/${cleanIdentity}.png`);
    }
    possibleAvatars.push("data:image/gif;base64,");

    let image = this.$refs.avatarPreview;
    image.possible = possibleAvatars;

    avatarPreview.onerror = e => {
      this.tryLoad(e.target, 1);
    };

    image.currentIndex = 0;

    if (image.currentIndex < image.possible.length) {
      image.src = image.possible[image.currentIndex];
    } else {
      avatarPreview.onerror = null;
      image.src = image.dataset.fallbacksrc;
    }
  },
  tryLoad(image, increment) {
    if (increment) {
      image.currentIndex += increment;
    }

    if (image.currentIndex < image.possible.length) {
      image.src = image.possible[image.currentIndex];
    } else {
      image.onerror = null;
      image.src = image.dataset.fallbacksrc;
    }
  },
  postComment() {
    let button = this.$refs.commentbutton;
    var status = this.$refs.commentstatus;
    let image = this.$refs.avatarPreview;
    status.innerText = "";
    status.classList.remove('visible');

    var missing = Array.from(document.querySelectorAll("[data-required]"))
      .filter(el => el.value === "")
      .map(el => el.name);
    if (missing.length > 0) {
      status.classList.add('visible');
      status.innerText =
        "Some required fields are missing - (" + missing.join(", ") + ")";
      return;
    }
    
    if (button.innerText != "CONFIRM") {
      button.setAttribute(
        "title",
        "Click the button again to confirm your comment"
      );
      button.classList.add("confirm-button");
      button.innerText = "Confirm";
      this.checkAvatar();
      return;
    }

    this.$refs.avatar.value = image.src;
    this.formData.identity = "";
    button.setAttribute("disabled", "disabled");
    button.innerText = "Posting...";
    button.classList.remove("confirm-button");
    this.$refs.commentform.submit();
  }
}
};
</script>
<style lang="scss">
.new-comment {
  margin-bottom: 20px;
  .avatar {
    display:none;
  }
}
</style>